package javapackage;

public class IfElseClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a =22;
		
		if(a%2 == 0)
		{
			System.out.println("It is a even number");
		}
		else
		{
			System.out.println("It is odd number");
		}
		
	}

}
